mapper.select:
SELECT COUNT(*) 
FROM comments
WHERE comment_post_id = #{post_id}

select post_id, post_content, post_user_email, post_add_date, post_modify_date from test.posts
where post_content like concat('%',#{keyword}, '%')
order by post_content desc limit #{skip}, #{amount}

SELECT *
FROM comments c
WHERE c.comment_post_id = #{post_id}
ORDER BY c.comment_add_date ASC
LIMIT 1;

SELECT u.user_name
FROM comments c
JOIN users u ON c.comment_user_email = u.user_email
WHERE c.comment_post_id = #{post_id}
ORDER BY c.comment_add_date ASC
LIMIT 1;
